<script>
	import { onMount } from 'svelte';

	let theme = $state('system');
	let systemTheme = $state('');

	onMount(() => {
		const selectedTheme = localStorage.getItem('theme') ?? 'system';
		systemTheme = window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light';
		theme = selectedTheme === 'system' ? systemTheme : selectedTheme;
	});

	function setDarkTheme() {
		theme = 'dark';
		localStorage.setItem('theme', 'dark');
		document.documentElement.classList.add('dark');
	}

	function setLightTheme() {
		theme = 'light';
		localStorage.setItem('theme', 'light');
		document.documentElement.classList.remove('dark');
	}

	// function setSystemTheme() {
	// 	theme = systemTheme;
	// 	localStorage.removeItem('theme');
	// 	document.documentElement.classList.remove('dark');
	// }

	function toggleTheme() {
		if (theme === 'light') {
			setDarkTheme();
		} else {
			setLightTheme();
		}
	}
</script>

<svelte:head>
	<!-- Prevent Chromium based browser from inverting the page. -->
	<meta name="color-scheme" content="light" />
	<!-- Prevent Dark Reader from inverting the page. -->
	<meta name="darkreader-lock" />
	<!-- Load theme before body starts rendering. -->
	<script>
		const selectedTheme = localStorage.getItem('theme') ?? 'system';
		const systemTheme = window.matchMedia('(prefers-color-scheme: dark)').matches
			? 'dark'
			: 'light';
		const theme = selectedTheme !== 'system' ? selectedTheme : systemTheme;
		if (theme === 'dark') {
			document.documentElement.classList.add('dark');
		}
	</script>
</svelte:head>

